package org.example;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

public class HammTest {
    @ParameterizedTest
    @MethodSource("dataHamm")
    public void testHamm(String dna1, String dna2, int expected) {
        int actual = Hamm.calc(dna1, dna2);
        Assertions.assertEquals(expected, actual);
    }

    private static Stream<Arguments> dataHamm() {
        return Stream.of(
                Arguments.of("GAGCCTACTAACGGGAT", "CATCGTAATGACGGCCT", 7),
                Arguments.of("", "", 490)
        );
    }
}
